SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_requisicao_mat_atual ON pls_requisicao_mat CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_requisicao_mat_atual() RETURNS trigger AS $BODY$
declare

cd_material_w	integer;
detalhes_exec_w		varchar(4000);

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S')  then
	if (NEW.nr_seq_material is not null) then
		select	cd_material
		into STRICT	cd_material_w
		from	pls_material a
		where	a.nr_sequencia	= NEW.nr_seq_material;

		NEW.cd_material	:= cd_material_w;
	end if;
end if;

if (NEW.qt_solicitado = 0) then
	detalhes_exec_w	:= substr(pls_obter_detalhe_exec(true),1,4000);

	insert into pls_requisicao_historico(nr_sequencia,
			nr_seq_requisicao,
			dt_historico,
			dt_atualizacao,
			nm_usuario,
			ds_historico,
			ie_origem_historico,
			ie_tipo_historico)
	values (nextval('pls_requisicao_historico_seq'),
			NEW.nr_seq_requisicao,
			LOCALTIMESTAMP,
			LOCALTIMESTAMP,
			NEW.nm_usuario,
			detalhes_exec_w,
			'A',
			'L');

end if;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_requisicao_mat_atual() FROM PUBLIC;

CREATE TRIGGER pls_requisicao_mat_atual
	BEFORE INSERT OR UPDATE ON pls_requisicao_mat FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_requisicao_mat_atual();

