SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_requisicao_proc_insert ON pls_requisicao_proc CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_requisicao_proc_insert() RETURNS trigger AS $BODY$
declare

nr_seq_pacote_w			bigint;
ie_regra_preco_w		varchar(3);
nr_seq_regra_pacote_out_w	bigint;
cd_proc_pacote_out_w		bigint	:= null;
ie_origem_pacote_out_w		bigint	:= null;
vl_pacote_out_w			double precision	:= 0;
vl_medico_out_w			double precision	:= 0;
vl_anestesista_out_w		double precision	:= 0;
vl_auxiliares_out_w		double precision	:= 0;
vl_custo_operacional_out_w	double precision	:= 0;
vl_materiais_out_w		double precision	:= 0;
nr_seq_intercambio_w		bigint;
nr_seq_classificacao_prest_w	bigint;
nr_seq_protocolo_w		bigint;
dt_conta_guia_w			timestamp;
nr_seq_segurado_w		bigint;
cd_guia_w			varchar(30);
sg_estado_w			pessoa_juridica.sg_estado%type;
sg_estado_int_w			pessoa_juridica.sg_estado%type;
nr_seq_prestador_prot_w		bigint;
cd_estabelecimento_w		smallint;
nr_seq_tipo_acomod_w		bigint;
dt_pacote_w			timestamp;
ie_internado_w			varchar(3);
nr_seq_plano_w			bigint;
nr_contrato_w			bigint;
nr_seq_congenere_w		bigint;
ie_origem_conta_w		varchar(5)	:= null;
ie_tipo_intercambio_w		varchar(5);
nr_seq_conta_w			bigint;
nr_seq_prestador_conta_w	bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
nr_seq_prestador_w		bigint;
nr_seq_requisicao_w		bigint;
ie_tipo_processo_w		varchar(2);
ie_tipo_intercambio_ww		pls_requisicao.ie_tipo_intercambio%type;
ie_ident_pacote_ptu_w		pls_param_intercambio_scs.ie_ident_pacote_ptu%type;
ie_pacote_maior_vl_w		pls_param_requisicao.ie_pacote_maior_vl%type;
ie_tipo_guia_w			pls_requisicao.ie_tipo_guia%type;
BEGIN
  BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'N') then
    RETURN NEW;
end if;

select	max(nr_sequencia)
into STRICT	nr_seq_pacote_w
from	pls_pacote
where	cd_procedimento		= NEW.cd_procedimento
and	ie_origem_proced	= NEW.ie_origem_proced
and	ie_situacao		= 'A';

if (nr_seq_pacote_w	is not null) then
	select	coalesce(ie_regra_preco,'N')
	into STRICT	ie_regra_preco_w
	from	pls_pacote a
	where	a.nr_sequencia = nr_seq_pacote_w;

	nr_seq_requisicao_w	:= NEW.nr_seq_requisicao;

	/*Obter dados da guia*/


	select	coalesce(nr_seq_prestador_exec,nr_seq_prestador),
		cd_estabelecimento,
		dt_requisicao,
		nr_seq_tipo_acomodacao,
		nr_seq_plano,
		pls_obter_internado_guia(nr_sequencia,'R'),
		nr_seq_segurado,
		ie_tipo_processo,
		coalesce(ie_tipo_intercambio, 'N'),
		ie_tipo_guia
	into STRICT	nr_seq_prestador_w,
		cd_estabelecimento_w,
		dt_pacote_w,
		nr_seq_tipo_acomod_w,
		nr_seq_plano_w,
		ie_internado_w,
		nr_seq_segurado_w,
		ie_tipo_processo_w,
		ie_tipo_intercambio_ww,
		ie_tipo_guia_w
	from	pls_requisicao
	where	nr_sequencia	= nr_seq_requisicao_w;

	if (ie_regra_preco_w = 'S') then
		if (nr_seq_tipo_acomod_w is null) then
			nr_seq_tipo_acomod_w := pls_obter_dados_produto(nr_seq_plano_w,'AP');
		end if;

		if (coalesce(nr_seq_plano_w,0) = 0) then
			BEGIN
			select	a.nr_seq_plano
			into STRICT	nr_seq_plano_w
			from	pls_segurado			a
			where	a.nr_sequencia = nr_seq_segurado_w;
			exception
			when others then
				nr_seq_plano_w := null;
			end;
		end if;

		BEGIN
		select	coalesce(a.nr_contrato,0),
			b.nr_seq_congenere
		into STRICT	nr_contrato_w,
			nr_seq_congenere_w
		from	pls_contrato	a,
			pls_segurado	b
		where	a.nr_sequencia	= b.nr_seq_contrato
		and	b.nr_sequencia	= nr_seq_segurado_w;
		exception
		when others then
			nr_contrato_w		:= null;
			nr_seq_congenere_w	:= null;
		end;

		if (nr_contrato_w is null) then
			nr_contrato_w := 0;
		end if;

		if (nr_seq_congenere_w is null) then
			BEGIN
				select	b.nr_seq_congenere
				into STRICT	nr_seq_congenere_w
				from	pls_segurado	b
				where	b.nr_sequencia	= nr_seq_segurado_w;
			exception
			when others then
				nr_seq_congenere_w	:= null;
			end;
		end if;

		select	max(nr_seq_intercambio)
		into STRICT	nr_seq_intercambio_w
		from	pls_segurado
		where	nr_sequencia	= nr_seq_segurado_w;

		select	max(nr_seq_classificacao)
		into STRICT	nr_seq_classificacao_prest_w
		from	pls_prestador
		where	nr_sequencia = nr_seq_prestador_w;

		select	coalesce(max(sg_estado),'X')
		into STRICT	sg_estado_w
		from	pessoa_juridica
		where	cd_cgc	= (	SELECT	max(cd_cgc_outorgante)
					from	pls_outorgante
					where	cd_estabelecimento	= cd_estabelecimento_w);
		select	coalesce(max(a.sg_estado),'X')
		into STRICT	sg_estado_int_w
		from	pessoa_juridica	a,
			pls_congenere	b
		where	a.cd_cgc	= b.cd_cgc
		and	b.nr_sequencia	= nr_seq_congenere_w;

		if (sg_estado_w <> 'X') and (sg_estado_int_w <> 'X') then
			if (sg_estado_w	= sg_estado_int_w) then
				ie_tipo_intercambio_w	:= 'E';
			else
				ie_tipo_intercambio_w	:= 'N';
			end if;
		else
			ie_tipo_intercambio_w	:= 'A';
		end if;

		SELECT * FROM pls_define_preco_pacote(cd_estabelecimento_w, nr_seq_prestador_w, nr_seq_tipo_acomod_w, dt_pacote_w, NEW.cd_procedimento, NEW.ie_origem_proced, ie_internado_w, nr_seq_plano_w, nr_contrato_w, nr_seq_congenere_w, NEW.nm_usuario, ie_origem_conta_w, ie_tipo_intercambio_w, nr_seq_pacote_w, nr_seq_regra_pacote_out_w, cd_proc_pacote_out_w, ie_origem_pacote_out_w, vl_pacote_out_w, vl_medico_out_w, vl_anestesista_out_w, vl_auxiliares_out_w, vl_custo_operacional_out_w, vl_materiais_out_w, nr_seq_intercambio_w, nr_seq_classificacao_prest_w, null, nr_seq_segurado_w, null, 'N', null, 1) INTO STRICT nr_seq_pacote_w, nr_seq_regra_pacote_out_w, cd_proc_pacote_out_w, ie_origem_pacote_out_w, vl_pacote_out_w, vl_medico_out_w, vl_anestesista_out_w, vl_auxiliares_out_w, vl_custo_operacional_out_w, vl_materiais_out_w;
	end if;

	BEGIN
		select	coalesce(ie_pacote_maior_vl,'N')
		into STRICT	ie_pacote_maior_vl_w
		from	pls_param_requisicao;
	exception
	when others then
		ie_pacote_maior_vl_w	:= 'N';
	end;

	if (ie_pacote_maior_vl_w	= 'S') then

		select	max(nr_sequencia)
		into STRICT	nr_seq_pacote_w
		from	pls_pacote
		where	cd_procedimento		= NEW.cd_procedimento
		and	ie_origem_proced	= NEW.ie_origem_proced
		and	ie_situacao		= 'A';

		if (nr_seq_pacote_w is not null) then
			select	max(a.nr_sequencia)
			into STRICT	nr_seq_regra_pacote_out_w
			from	pls_pacote_tipo_acomodacao a
			where 	a.nr_seq_pacote = nr_seq_pacote_w
			and   	a.vl_pacote       = (	SELECT  max(x.vl_pacote)
							from    pls_pacote_tipo_acomodacao x
							where   x.nr_seq_pacote = nr_seq_pacote_w
							and	((x.dt_fim_vigencia is null) or (trunc(x.dt_fim_vigencia) >= trunc(LOCALTIMESTAMP))));

			BEGIN
				select	vl_pacote
				into STRICT	vl_pacote_out_w
				from	pls_pacote_tipo_acomodacao
				where	nr_sequencia	= nr_seq_regra_pacote_out_w;
			exception
			when others then
				vl_pacote_out_w := null;
			end;

		end if;
	end if;


	if (nr_seq_regra_pacote_out_w is not null) then
		NEW.vl_procedimento		:= vl_pacote_out_w;
		NEW.nr_seq_pacote		:= nr_seq_pacote_w;
		NEW.nr_seq_preco_pacote	:= nr_seq_regra_pacote_out_w;
	end if;

	select 	max(coalesce(ie_ident_pacote_ptu, 'N'))
	into STRICT	ie_ident_pacote_ptu_w
	from	pls_param_intercambio_scs;

	if	((nr_seq_pacote_w	is not null) and (ie_tipo_processo_w	= 'I' and ie_tipo_intercambio_ww = 'I') and (ie_ident_pacote_ptu_w = 'S'))then
		NEW.ie_pacote_ptu	:= 'S';
	end if;

	if	((nr_seq_pacote_w	is not null) and (ie_tipo_processo_w	= 'I' and ie_tipo_intercambio_ww = 'I')
		and (NEW.cd_procedimento_ptu is null)
    )then
		NEW.vl_procedimento	:= 0;
		NEW.vl_total_pacote	:= 0;
	end if;
end if;

  END;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_requisicao_proc_insert() FROM PUBLIC;

CREATE TRIGGER pls_requisicao_proc_insert
	BEFORE INSERT ON pls_requisicao_proc FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_requisicao_proc_insert();

