DROP TRIGGER IF EXISTS pls_requisicao_update ON pls_requisicao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_requisicao_update() RETURNS trigger AS $BODY$
declare
nr_seq_prot_atend_w	pls_protocolo_atendimento.nr_sequencia%type;
BEGIN
  BEGIN
BEGIN
	select	nr_sequencia
	into STRICT	nr_seq_prot_atend_w
	from	pls_protocolo_atendimento
	where 	nr_seq_requisicao	= NEW.nr_sequencia;
exception
when others then
	nr_seq_prot_atend_w	:= null;
end;

if (nr_seq_prot_atend_w is not null) then
	if (coalesce(OLD.ie_estagio, 0) <> coalesce(NEW.ie_estagio, 0)) and (OLD.ie_estagio in (4,5,8,10)) and (NEW.ie_estagio in (2,6,7)) then

		update	pls_protocolo_atendimento
		set	ie_status	= 3,
			nm_usuario 	= NEW.nm_usuario,
			dt_atualizacao 	= LOCALTIMESTAMP
		where	nr_sequencia	= nr_seq_prot_atend_w;

	elsif (coalesce(OLD.ie_estagio, 0) <> coalesce(NEW.ie_estagio, 0)) and (NEW.ie_estagio = 3) then

		update	pls_protocolo_atendimento
		set	ie_status	= 4,
			nm_usuario 	= NEW.nm_usuario,
			dt_atualizacao 	= LOCALTIMESTAMP
		where	nr_sequencia	= nr_seq_prot_atend_w;
	end if;
end if;

  END;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_requisicao_update() FROM PUBLIC;

CREATE TRIGGER pls_requisicao_update
	BEFORE UPDATE ON pls_requisicao FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_requisicao_update();

