CREATE OR REPLACE FUNCTION trigger_fct_pls_simulacao_resumo_delete() RETURNS trigger AS $BODY$
declare

qt_reg_benef_del_w	bigint;
qt_reg_benef_del_ww	bigint;

BEGIN

select	count(*)
into STRICT	qt_reg_benef_del_w
from	pls_simulacao_resumo
where	nr_seq_simulacao	= OLD.nr_seq_simulacao
and	nr_seq_ordem		in (1,2,3,4,5)
and	nr_seq_segurado_simul	= OLD.nr_sequencia;

if (qt_reg_benef_del_w > 0) then
	delete	FROM pls_simulacao_resumo
	where	nr_seq_simulacao	= OLD.nr_seq_simulacao
	and	nr_seq_ordem		in (1,2,3,4,5)
	and	nr_seq_segurado_simul	= OLD.nr_sequencia;

	delete	FROM pls_bonificacao_vinculo
	where	nr_seq_segurado_simul	= OLD.nr_sequencia;

	delete	FROM pls_sca_vinculo
	where	nr_seq_segurado_simul	= OLD.nr_sequencia;
end if;

RETURN OLD;
end
$BODY$
