DROP TRIGGER IF EXISTS pls_solicitacao_comercial_ins ON pls_solicitacao_comercial CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_solicitacao_comercial_ins() RETURNS trigger AS $BODY$
declare
 
BEGIN 
 
if (NEW.nr_seq_pac_senha_fila is not null) then 
	update	paciente_senha_fila 
	set	dt_vinculacao_senha 	= LOCALTIMESTAMP, 
		nm_usuario		= coalesce(NEW.nm_usuario, wheb_usuario_pck.get_nm_usuario), 
		nm_paciente		= CASE WHEN NEW.cd_pf_vinculado = NULL THEN NEW.nm_pessoa_fisica WHEN NEW.cd_pf_vinculado=substr(obter_nome_pf(NEW.cd_pf_vinculado),1,255) THEN null END , 
		cd_pessoa_fisica 	= NEW.cd_pf_vinculado 
	where	nr_sequencia 		= NEW.nr_seq_pac_senha_fila;
end if;
 
CALL pls_lanc_aut_historico_lead(NEW.nr_sequencia, NEW.ie_origem_solicitacao,NEW.nr_acao_origem,NEW.dt_vencimento_contrato,NEW.nm_usuario);
 
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_solicitacao_comercial_ins() FROM PUBLIC;

CREATE TRIGGER pls_solicitacao_comercial_ins
	AFTER INSERT ON pls_solicitacao_comercial FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_solicitacao_comercial_ins();

