DROP TRIGGER IF EXISTS pls_solicitacao_rescisao_befin ON pls_solicitacao_rescisao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_solicitacao_rescisao_befin() RETURNS trigger AS $BODY$
declare

ie_tipo_contratacao_permitid_w		varchar(2);
ie_exibir_mensagem_w			varchar(1);

BEGIN

ie_tipo_contratacao_permitid_w	:= coalesce(obter_valor_param_usuario(268, 17, Obter_Perfil_Ativo, NEW.nm_usuario, wheb_usuario_pck.get_cd_estabelecimento), 'T');
ie_exibir_mensagem_w	:= 'N';

if (ie_tipo_contratacao_permitid_w <> 'T') then
	if (ie_tipo_contratacao_permitid_w = 'C') then
		if (NEW.ie_tipo_contratacao not in ('CE', 'CA')) then
			ie_exibir_mensagem_w	:= 'S';
		end if;
	elsif (ie_tipo_contratacao_permitid_w <> NEW.ie_tipo_contratacao) then
		ie_exibir_mensagem_w	:= 'S';
	end if;
	
	if (ie_exibir_mensagem_w = 'S') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1122527, 'DS_TIPO_CONTRATACAO='|| obter_valor_dominio(1666,NEW.ie_tipo_contratacao));
	end if;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_solicitacao_rescisao_befin() FROM PUBLIC;

CREATE TRIGGER pls_solicitacao_rescisao_befin
	BEFORE INSERT OR UPDATE ON pls_solicitacao_rescisao FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_solicitacao_rescisao_befin();

