SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_solicitacao_rescisao_ins ON pls_solicitacao_rescisao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_solicitacao_rescisao_ins() RETURNS trigger AS $BODY$
declare
qt_registro_w		integer;
nr_seq_contrato_w	pls_contrato.nr_sequencia%type;
BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S')  then
	if (TG_OP = 'UPDATE') and (NEW.nr_contrato <> OLD.nr_contrato) then
		select	count(1)
		into STRICT	qt_registro_w
		from	pls_solic_rescisao_benef
		where	nr_seq_solicitacao	= NEW.nr_sequencia;
		
		if (qt_registro_w > 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(783818); --Ja foram selecionados os beneficiarios. Nao sera possivel alterar o contrato!
		end if;
	end if;

	--Atualizar o contrato

	if (NEW.nr_contrato is not null) then
		select	max(nr_sequencia)
		into STRICT	nr_seq_contrato_w
		from	pls_contrato
		where	nr_contrato = NEW.nr_contrato;
	
	
		if (nr_seq_contrato_w is not null) then
			NEW.nr_seq_contrato := nr_seq_contrato_w;
		else
			CALL wheb_mensagem_pck.exibir_mensagem_abort(853575); --O contrato informado nao existe. Verifique!
		end if;
	else
		NEW.nr_seq_contrato	:= null;
	end if;

	if (NEW.nr_seq_contrato is not null) then
		NEW.ie_tipo_contratacao := pls_obter_dados_contrato(NEW.nr_seq_contrato,'TC');
	else	
		NEW.ie_tipo_contratacao := pls_obter_dados_intercambio(NEW.nr_seq_intercambio,'TC');
	end if;	
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_solicitacao_rescisao_ins() FROM PUBLIC;

CREATE TRIGGER pls_solicitacao_rescisao_ins
	BEFORE INSERT OR UPDATE ON pls_solicitacao_rescisao FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_solicitacao_rescisao_ins();

