SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS pls_solic_prop_pag_befinsup ON pls_solic_prop_pagamento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_pls_solic_prop_pag_befinsup() RETURNS trigger AS $BODY$
declare

qt_origem_w	bigint;
qt_plano_w bigint;

pragma autonomous_transaction;

BEGIN

if (NEW.nr_seq_plano is null) then
	select	count(1)
	into STRICT	qt_origem_w
	from	pls_solic_prop_pagamento
	where	cd_estabelecimento = NEW.cd_estabelecimento
	and	ie_origem = NEW.ie_origem
	and	nr_sequencia <> NEW.nr_sequencia
	and nr_seq_plano is null;
else
	select 	count(1)
	into STRICT 		qt_plano_w
	from 	pls_solic_prop_pagamento
	where cd_estabelecimento = NEW.cd_estabelecimento
	and nr_seq_plano = NEW.nr_seq_plano
	and	ie_origem = NEW.ie_origem
	and	nr_sequencia <> NEW.nr_sequencia;
end if;

if (coalesce(qt_origem_w, 0) > 0 or coalesce(qt_plano_w, 0) > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1092518); -- Ja ha regra para essa origem.
end if;

if	(NEW.ie_origem = 2 AND NEW.ie_cartao_debito = 'S') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1092520); -- A forma de pagamento "Cartao de debito" nao pode ser selecionada para a origem "Demais mensalidades".
elsif (NEW.ie_origem = 1 and NEW.ie_debito_automatico = 'S') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1117061); -- A forma de pagamento "Debito automatico" nao pode ser selecionada para a origem "Primeira mensalidade".
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_pls_solic_prop_pag_befinsup() FROM PUBLIC;

CREATE TRIGGER pls_solic_prop_pag_befinsup
	BEFORE INSERT OR UPDATE ON pls_solic_prop_pagamento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_pls_solic_prop_pag_befinsup();

