SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS posicao_cirurgia_pend_atual ON posicao_cirurgia CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_posicao_cirurgia_pend_atual() RETURNS trigger AS $BODY$
declare

qt_reg_w			smallint;
ie_tipo_w			varchar(10);
ie_libera_posicao_w		varchar(10);
cd_pessoa_fisica_w		varchar(10);
nr_atendimento_w	bigint;

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

select	max(ie_libera_posicao)
into STRICT	ie_libera_posicao_w
from	parametro_medico
where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

select	max(c.nr_atendimento),
	max(c.cd_pessoa_fisica)
into STRICT	nr_atendimento_w,
	cd_pessoa_fisica_w
from	cirurgia c
where	c.nr_cirurgia = NEW.nr_cirurgia;

if (cd_pessoa_fisica_w is null) then
	select	max(c.nr_atendimento),
		max(c.cd_pessoa_fisica)
	into STRICT	nr_atendimento_w,
		cd_pessoa_fisica_w
	from	pepo_cirurgia c
	where	c.nr_sequencia = NEW.nr_seq_pepo;
end if;

if (coalesce(ie_libera_posicao_w,'N') = 'S') then
	if (NEW.dt_liberacao is null) then
		ie_tipo_w := 'PO';
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then


		ie_tipo_w := 'XPO';
	end if;

	if (ie_tipo_w	is not null) then
		CALL Gerar_registro_pendente_PEP(ie_tipo_w, NEW.nr_sequencia, cd_pessoa_fisica_w, nr_atendimento_w, NEW.nm_usuario);
	end if;
end if;

<<Final>>
qt_reg_w	:= 0;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_posicao_cirurgia_pend_atual() FROM PUBLIC;

CREATE TRIGGER posicao_cirurgia_pend_atual
	AFTER INSERT OR UPDATE ON posicao_cirurgia FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_posicao_cirurgia_pend_atual();

