SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS preco_material_update ON preco_material CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_preco_material_update() RETURNS trigger AS $BODY$
declare

qt_mat_justif_w		bigint	:= 0;

BEGIN

if (OLD.cd_material <> NEW.cd_material) or (OLD.dt_inicio_vigencia <> NEW.dt_inicio_vigencia) then

	select	count(*)
	into STRICT	qt_mat_justif_w
	from	preco_mat_justificativa
	where	cd_estabelecimento = OLD.cd_estabelecimento
	and	cd_tab_preco_mat = OLD.cd_tab_preco_mat
	and	cd_material = OLD.cd_material
	and	dt_vigencia = OLD.dt_inicio_vigencia;

	if (qt_mat_justif_w > 0) then

		update	preco_mat_justificativa
		set	cd_material = NEW.cd_material,
			dt_vigencia = NEW.dt_inicio_vigencia
		where	cd_estabelecimento = OLD.cd_estabelecimento
		and	cd_tab_preco_mat = OLD.cd_tab_preco_mat
		and	cd_material = OLD.cd_material
		and	dt_vigencia = OLD.dt_inicio_vigencia;

	end if;

end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_preco_material_update() FROM PUBLIC;

CREATE TRIGGER preco_material_update
	AFTER UPDATE ON preco_material FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_preco_material_update();

