SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS prescr_dieta_hor_atual ON prescr_dieta_hor CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_prescr_dieta_hor_atual() RETURNS trigger AS $BODY$
declare
nr_seq_cpoe_w		prescr_dieta.nr_seq_dieta_cpoe%type;
ie_verificado_w		varchar(1);

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger	= 'S')  then
	if (TG_OP = 'INSERT') then

		select	max(coalesce(nr_seq_dieta_cpoe,0))
		into STRICT	nr_seq_cpoe_w
		from	prescr_dieta
		where	nr_sequencia = NEW.nr_seq_dieta
		and	nr_prescricao = NEW.nr_prescricao;

		if (nr_seq_cpoe_w) > 0 then
			select max(coalesce('S', 'N'))
			into STRICT   ie_verificado_w
			from   registro_validacao_adep
			where  nr_seq_cpoe = nr_seq_cpoe_w;
		end if;

		if (ie_verificado_w = 'S') then
			NEW.ie_ciente := 'S';
		end if;

	end if;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_prescr_dieta_hor_atual() FROM PUBLIC;

CREATE TRIGGER prescr_dieta_hor_atual
	BEFORE INSERT OR UPDATE ON prescr_dieta_hor FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_prescr_dieta_hor_atual();

