DROP TRIGGER IF EXISTS prescr_gasoterapia_evto_update ON prescr_gasoterapia_evento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_prescr_gasoterapia_evto_update() RETURNS trigger AS $BODY$
BEGIN

  if ((coalesce(OLD.ie_evento_valido,'S') = 'S')
      and (coalesce(NEW.ie_evento_valido,'S') = 'N')) then

    update EVOLUCAO_PACIENTE
	set ie_situacao 	= 'I',
	dt_inativacao 	= LOCALTIMESTAMP,
	nm_usuario_inativacao  = NEW.nm_usuario
    where cd_evolucao 	= OLD.cd_evolucao
	and ie_situacao 	= 'A';
  end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_prescr_gasoterapia_evto_update() FROM PUBLIC;

CREATE TRIGGER prescr_gasoterapia_evto_update
	AFTER UPDATE OF ie_evento_valido ON prescr_gasoterapia_evento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_prescr_gasoterapia_evto_update();

