SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS prescr_gaso_update_hl7_dixtal ON prescr_gasoterapia CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_prescr_gaso_update_hl7_dixtal() RETURNS trigger AS $BODY$
declare
 
nr_seq_interno_w		bigint;
ds_sep_bv_w		varchar(100);
ds_param_integ_hl7_w	varchar(4000);
nr_atendimento_w		bigint;
cd_pessoa_fisica_w	bigint;
ie_leito_monit		varchar(1);
 
BEGIN 
 
if (coalesce(OLD.ie_suspenso,'N') = 'N') and (NEW.ie_suspenso = 'S') then 
 
	ds_sep_bv_w := obter_separador_bv;
	 
	select	nr_atendimento, cd_pessoa_fisica 
	into STRICT	nr_atendimento_w, cd_pessoa_fisica_w 
	from 	prescr_medica 
	where 	nr_prescricao = NEW.nr_prescricao;
	 
	select	max(obter_atepacu_paciente(nr_atendimento_w ,'A')) 
	into STRICT	nr_seq_interno_w 
	;
	 
	select	coalesce(obter_se_leito_atual_monit(nr_atendimento_w),'N') 
	into STRICT	ie_leito_monit 
	;
	 
	if (ie_leito_monit = 'S') then 
		BEGIN		 
		ds_param_integ_hl7_w :=	'cd_pessoa_fisica=' || cd_pessoa_fisica_w  || ds_sep_bv_w || 
					'nr_atendimento='  || nr_atendimento_w   || ds_sep_bv_w || 
					'nr_seq_interno='   || nr_seq_interno_w    || ds_sep_bv_w || 
					'nr_prescricao='    || NEW.nr_prescricao   || ds_sep_bv_w || 
					'nr_seq_gaso='    || NEW.nr_sequencia   || ds_sep_bv_w;	
		CALL gravar_agend_integracao(50, ds_param_integ_hl7_w);	
		end;
	end if;	
	 
end if;
 
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_prescr_gaso_update_hl7_dixtal() FROM PUBLIC;

CREATE TRIGGER prescr_gaso_update_hl7_dixtal
	AFTER UPDATE ON prescr_gasoterapia FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_prescr_gaso_update_hl7_dixtal();

