SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS prescr_material_atual_hl7 ON prescr_material CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_prescr_material_atual_hl7() RETURNS trigger AS $BODY$
declare
ds_sep_bv_w		varchar(100);
ds_param_integ_hl7_w	varchar(4000);
nr_sequencia_w		bigint;
cd_pessoa_fisica_w	varchar(10);
dt_confirmacao_w	timestamp;
ie_gerar_integracao_w	varchar(1);
 
BEGIN 
 
-- Ocorre mutante no delete 
 
ds_sep_bv_w := obter_separador_bv;
 
if (TG_OP = 'INSERT') or (TG_OP = 'DELETE') or (OLD.cd_material <> NEW.cd_material) then 
 
	select	max(a.nr_sequencia), 
		max(dt_confirmacao), 
		max(ie_integra_rel_dixtal) 
	into STRICT	nr_sequencia_w, 
		dt_confirmacao_w, 
		ie_gerar_integracao_w 
	from	agenda b, 
		cirurgia c, 
		agenda_paciente a 
	where	a.cd_agenda = b.cd_agenda 
	and	a.nr_cirurgia = c.nr_cirurgia 
	and	c.nr_prescricao = NEW.nr_prescricao;
 
	if (nr_sequencia_w > 0) and (dt_confirmacao_w is not null) and (ie_gerar_integracao_w = 'S') then 
		select	max(cd_pessoa_fisica) 
		into STRICT	cd_pessoa_fisica_w 
		from	agenda_paciente 
		where	nr_sequencia = nr_sequencia_w;
		 
		if (cd_pessoa_fisica_w is not null) then 
			ds_param_integ_hl7_w :=	'cd_pessoa_fisica=' || cd_pessoa_fisica_w || ds_sep_bv_w || 
						'nr_sequencia=' || nr_sequencia_w || ds_sep_bv_w;
			CALL gravar_agend_integracao(193, ds_param_integ_hl7_w);		
		end if;
	end if;
	 
 
end if;
 
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_prescr_material_atual_hl7() FROM PUBLIC;

CREATE TRIGGER prescr_material_atual_hl7
	AFTER INSERT OR UPDATE ON prescr_material FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_prescr_material_atual_hl7();

