DROP TRIGGER IF EXISTS prescr_material_enf_upd ON prescr_material CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_prescr_material_enf_upd() RETURNS trigger AS $BODY$
DECLARE
ie_via_aplicacao_w			prescr_material.ie_via_aplicacao%type;

pragma autonomous_transaction;

BEGIN

if	((wheb_usuario_pck.get_cd_funcao in (7015,7010)) and (NEW.nr_agrupamento <> OLD.nr_agrupamento) and (NEW.ie_agrupador = 1)) then

	select	max(ie_via_aplicacao)
	into STRICT	ie_via_aplicacao_w
	from	prescr_material
	where	nr_prescricao = NEW.nr_prescricao
	and		nr_agrupamento = NEW.nr_agrupamento
	and		nr_sequencia <> NEW.nr_sequencia
	and		nr_seq_substituto is null;

end if;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
