SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS prescr_procedimento_aft_update ON prescr_procedimento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_prescr_procedimento_aft_update() RETURNS trigger AS $BODY$
declare
ie_envia_mensagem_w        varchar(1);
ie_matrix_status_envio_integ_w  lab_parametro.ie_matrix_status_envio_integ%type;
ie_agrupada_w          varchar(1);

BEGIN

if (wheb_usuario_pck.is_evento_ativo(288) = 'S') and (NEW.ie_status_atend < 20) and (coalesce(OLD.ie_suspenso,'N') = 'N') and (coalesce(NEW.ie_suspenso,'N') = 'S') then

  CALL integrar_softlab_ws(  288,
              null,
              NEW.nr_prescricao,
              NEW.nr_sequencia,
              NEW.nr_seq_exame,
              NEW.nm_usuario,
              2,
              'N');
end if;

if  ((wheb_usuario_pck.is_evento_ativo(414) = 'S') or (obter_se_integracao_ativa(886,53) = 'S')) and (NEW.nr_seq_exame is not null) and (coalesce(OLD.ie_suspenso,'N') = 'N') and (coalesce(NEW.ie_suspenso,'N') = 'S') then

  select  max(a.IE_MATRIX_STATUS_ENVIO_INTEG)
  into STRICT  ie_matrix_status_envio_integ_w
  from  lab_parametro a
  where  a.cd_estabelecimento = obter_valores_prescr_trigger(NEW.nr_prescricao,'E');

  if (ie_matrix_status_envio_integ_w is null) or (NEW.ie_status_atend >= ie_matrix_status_envio_integ_w) then
    if (wheb_usuario_pck.is_evento_ativo(414) = 'S') then
           CALL integrar_matrix_ws(414,
                              NEW.nr_prescricao,
                              NEW.nr_sequencia,
                              NEW.nr_seq_exame,
                              NEW.nm_usuario,
                              'N');
    end if;
  if (obter_se_integracao_ativa(886,53) = 'S') then
       if (obter_se_contido(OLD.ie_status_atend,Wheb_assist_pck.obterParametroFuncao(722,261)) = 'S') or (Wheb_assist_pck.obterParametroFuncao(722,261) is null) then
				
          CALL integrar_matrix_ws_v7(886, NEW.nr_prescricao, NEW.nr_sequencia,0,NEW.nr_seq_exame);
       else
          CALL wheb_mensagem_pck.exibir_mensagem_abort(1098715,'DS_STATUS='||OBTER_DS_STATUS_ATEND(NEW.ie_status_atend));
       end if;
    end if;
  end if;
end if;

if (wheb_usuario_pck.is_evento_ativo(541) = 'S') and (NEW.nr_seq_exame is not null) and (coalesce(OLD.ie_suspenso,'N') = 'N') and (coalesce(NEW.ie_suspenso,'N') = 'S') then

    select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
    into STRICT  ie_envia_mensagem_w
    from  lab_exame_equip b,
        equipamento_lab c,
        material_exame_lab d
    where  NEW.nr_seq_exame    = b.nr_seq_exame
    and    b.cd_equipamento  = c.cd_equipamento
    and    NEW.cd_material_exame = d.cd_material_exame
    and (b.nr_seq_material = d.nr_sequencia or b.nr_seq_material is null)
    and    c.ds_sigla = 'AGFAWS'
    and    NEW.ie_suspenso = 'S';

  if (ie_envia_mensagem_w = 'S') then

    CALL integrar_agfa_ws(  null,
              NEW.nr_prescricao,
              NEW.nr_sequencia,
              NEW.nm_usuario,
              'S',
              'N');
  end if;
end if;

/*Desvincular a prescrição e o atendimento da prevenda, quando suspende*/


if (OLD.dt_suspensao is null) and (NEW.dt_suspensao is not null) then

  select  coalesce(max(a.ie_agrupada),'N')
  into STRICT  ie_agrupada_w
  from  pre_venda a,
      pre_venda_item b
  where  a.nr_sequencia = b.nr_seq_pre_venda
  and    b.nr_prescricao = NEW.nr_prescricao;

  if (ie_agrupada_w = 'N') then
    update  pre_venda_item
    set    nr_atendimento  = NULL,
        nr_prescricao  = NULL,
        nr_seq_interno  = NULL
    where  nr_seq_interno = NEW.nr_seq_interno;
  end if;
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_prescr_procedimento_aft_update() FROM PUBLIC;

CREATE TRIGGER prescr_procedimento_aft_update
	BEFORE UPDATE ON prescr_procedimento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_prescr_procedimento_aft_update();

