SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS prescr_proc_update_hl7_dixtal ON prescr_procedimento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_prescr_proc_update_hl7_dixtal() RETURNS trigger AS $BODY$
declare
ds_sep_bv_w		varchar(100);
cd_pessoa_fisica_w	varchar(10);
nr_atendimento_w		bigint;
nr_seq_interno_w		bigint;
nr_seq_resultado_w		bigint;
nr_seq_result_item_w	bigint;
ds_param_integ_hl7_w	varchar(4000);
ie_leito_monit		varchar(1);
BEGIN
  BEGIN 
ds_sep_bv_w := obter_separador_bv;
 
if	(NEW.ie_status_atend	= 40 AND OLD.ie_status_atend	<> 40) then 
	BEGIN 
	BEGIN 
	 
	select	pm.cd_pessoa_fisica, 
		coalesce(pm.nr_atendimento,0), 
		coalesce(substr(obter_atepacu_paciente(coalesce(pm.nr_atendimento,0),'IA'),1,5),0), 
		elr.nr_seq_resultado, 
		elri.nr_sequencia 
	into STRICT	cd_pessoa_fisica_w, 
		nr_atendimento_w, 
		nr_seq_interno_w, 
		nr_seq_resultado_w, 
		nr_seq_result_item_w 
	from	prescr_medica pm, 
		exame_lab_resultado elr, 
		exame_lab_result_item elri 
	where	pm.nr_prescricao = elr.nr_prescricao 
	and 	elr.nr_seq_resultado = elri.nr_seq_resultado 
	and	elri.dt_aprovacao is not null 
	and	elri.nr_seq_prescr = NEW.nr_sequencia 
	and	pm.nr_prescricao = NEW.nr_prescricao;
	 
	select	coalesce(obter_se_leito_atual_monit(nr_atendimento_w),'N') 
	into STRICT	ie_leito_monit 
	;
	 
	if (ie_leito_monit = 'S') then 
		BEGIN 
		ds_param_integ_hl7_w :=	'cd_pessoa_fisica=' || cd_pessoa_fisica_w || ds_sep_bv_w || 
					'nr_atendimento=' || nr_atendimento_w || ds_sep_bv_w || 
					'nr_seq_interno=' || nr_seq_interno_w || ds_sep_bv_w || 
					'nr_seq_resultado=' || nr_seq_resultado_w || ds_sep_bv_w || 
					'nr_seq_prescr=' || NEW.nr_sequencia || ds_sep_bv_w || 
					'nr_seq_result_item=' || nr_seq_result_item_w || ds_sep_bv_w;						
		CALL gravar_agend_integracao(54, ds_param_integ_hl7_w);	
		end;
	end if;
	 
	exception 
	when others then 
		ds_param_integ_hl7_w := '';
	end;
	end;
end if;
  END;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_prescr_proc_update_hl7_dixtal() FROM PUBLIC;

CREATE TRIGGER prescr_proc_update_hl7_dixtal
	AFTER INSERT OR UPDATE ON prescr_procedimento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_prescr_proc_update_hl7_dixtal();

