DROP TRIGGER IF EXISTS prescr_solucao_evento_befins ON prescr_solucao_evento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_prescr_solucao_evento_befins() RETURNS trigger AS $BODY$
DECLARE

nr_atendimento_w	bigint;

BEGIN

if (NEW.ie_alteracao not in (1,2,3,4,5,9,12,13,16,19,24,25,26,27,28,31,33,37,57)) and (NEW.ds_observacao is not null) then
	NEW.ds_observacao := null;
end if;

select	coalesce(max(nr_atendimento),0)
into STRICT	nr_atendimento_w
from	prescr_medica
where	nr_prescricao = NEW.nr_prescricao;

if (nr_atendimento_w > 0) then
	NEW.nr_atendimento := nr_atendimento_w;
end if;

if (NEW.cd_funcao is null) then
	NEW.cd_funcao	:= obter_funcao_ativa;
end if;

if	wheb_usuario_pck.get_ie_executar_trigger = 'S' then
  if (coalesce(pkg_i18n.get_user_locale, 'pt_BR') = 'ja_JP'
      and NEW.ie_alteracao = 1
      and obter_taxa_adicional(NEW.nr_prescricao, NEW.nr_seq_solucao, NEW.qt_dosagem, NEW.ie_tipo_dosagem) = 'S'
      ) then
      NEW.si_additional_medical_fee := 'S';
  else
      NEW.si_additional_medical_fee := 'N';
  end if;
end if;
RETURN NEW;
END;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
