CREATE OR REPLACE FUNCTION trigger_fct_privacy_property_atual() RETURNS trigger AS $BODY$
declare

is_a_valid_expression	varchar(1);

BEGIN

if (NEW.ds_key in ('CD_EXP_CONFIG', 'CD_EXP_MESSAGE')) then

	select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'Y' END
	into STRICT	is_a_valid_expression
	from	dic_expressao
	where	cd_expressao = NEW.ds_value;

	if (is_a_valid_expression = 'N') then
		--A expressão informada não existe.
		CALL wheb_mensagem_pck.exibir_mensagem_abort(854529);
	end if;

end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
