SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS procedimento_paciente_afterins ON procedimento_paciente CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_procedimento_paciente_afterins() RETURNS trigger AS $BODY$
declare

ie_clinica_w	atendimento_paciente.ie_clinica%type;

BEGIN

if (coalesce((pkg_i18n.get_user_locale), 'pt_BR') = 'es_MX') then

	select	max(ie_clinica)
	into STRICT	ie_clinica_w
	from	atendimento_paciente
	where	nr_atendimento = NEW.nr_atendimento;

	CALL gerar_codificacao_atendimento(
		NEW.nr_atendimento,
		ie_clinica_w,
		NEW.nm_usuario,
		NEW.nr_sequencia,
		NEW.cd_doenca_cid,
		NEW.cd_procedimento,
		NEW.ie_origem_proced,
		'N',
		NEW.nr_cirurgia);

end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_procedimento_paciente_afterins() FROM PUBLIC;

CREATE TRIGGER procedimento_paciente_afterins
	AFTER INSERT ON procedimento_paciente FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_procedimento_paciente_afterins();

