DROP TRIGGER IF EXISTS procedimento_pac_medic_ins_upd ON procedimento_pac_medico CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_procedimento_pac_medic_ins_upd() RETURNS trigger AS $BODY$
declare
ie_situacao_w	procedimento.ie_situacao%type;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S')  then

	if (NEW.cd_procedimento is not null) and (NEW.ie_origem_proced is not null) then
		
		select	max(ie_situacao)
		into STRICT	ie_situacao_w
		from	procedimento
		where	cd_procedimento		= NEW.cd_procedimento
		and	ie_origem_proced	= NEW.ie_origem_proced;
		
		if (coalesce(ie_situacao_w,'A') = 'I') then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(1090883);
		end if;		
	end if;
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
