SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS proced_adic_update ON agenda_paciente_proc CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_proced_adic_update() RETURNS trigger AS $BODY$
declare
nr_cirurgia_w			bigint;
nr_prescricao_w			bigint;
ie_alterar_proced_adic_w	varchar(1);

BEGIN

ie_alterar_proced_adic_w := Obter_Param_Usuario(871, 335, obter_perfil_ativo, NEW.nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_alterar_proced_adic_w);

if (wheb_usuario_pck.get_ie_executar_trigger	= 'S')  and (ie_alterar_proced_adic_w = 'S') then

	select	max(nr_cirurgia)
	into STRICT	nr_cirurgia_w
	from	agenda_paciente
	where	nr_sequencia = OLD.nr_sequencia;

	select	max(nr_prescricao)
	into STRICT	nr_prescricao_w
	from	cirurgia
	where 	nr_cirurgia	=	nr_cirurgia_w;

	if	((NEW.nr_seq_proc_interno <> OLD.nr_seq_proc_interno) or (NEW.cd_procedimento <> OLD.cd_procedimento) or (NEW.ie_origem_proced <> OLD.ie_origem_proced)) then
		update	prescr_procedimento
		set	nr_seq_proc_interno 	= NEW.nr_seq_proc_interno,
			cd_procedimento		= NEW.cd_procedimento,
			ie_origem_proced	= NEW.ie_origem_proced
		where	nr_prescricao		= nr_prescricao_w
		and	coalesce(nr_seq_proc_interno,0) 	= coalesce(OLD.nr_seq_proc_interno,0)
		and	coalesce(cd_procedimento,0)		= coalesce(OLD.cd_procedimento,0)
		and	coalesce(ie_origem_proced,0)		= coalesce(OLD.ie_origem_proced,0);

	end if;
end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_proced_adic_update() FROM PUBLIC;

CREATE TRIGGER proced_adic_update
	AFTER UPDATE ON agenda_paciente_proc FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_proced_adic_update();

