BEGIN

if (coalesce(NEW.cd_medico_prev_laudo,'0')  <> coalesce(OLD.cd_medico_prev_laudo,'0')) then

	update procedimento_paciente
	set    cd_medico_prev_laudo = NEW.cd_medico_prev_laudo
