end if;

if (coalesce(NEW.cd_equipamento,0)  <> coalesce(OLD.cd_equipamento,0)) then

	update procedimento_paciente
	set    cd_equipamento = NEW.cd_equipamento
	where  nr_sequencia = NEW.nr_seq_proc;

end if;


RETURN NEW;
end
