DROP TRIGGER IF EXISTS proj_ata_before ON proj_ata CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_proj_ata_before() RETURNS trigger AS $BODY$
declare
nr_seq_cliente_w   com_cliente.nr_sequencia%type;
BEGIN
    if (wheb_usuario_pck.get_ie_executar_trigger = 'S') Then
        if (coalesce(NEW.nr_seq_projeto,0) <> coalesce(OLD.nr_seq_projeto,0)) then
            BEGIN
                select	max(nr_seq_cliente)
                into STRICT 	nr_seq_cliente_w
                from	proj_projeto
                where	nr_sequencia = NEW.nr_seq_projeto;

                NEW.nr_seq_cliente := nr_seq_cliente_w;
            end;
        end if;
    end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
