DROP TRIGGER IF EXISTS proj_cons_aval_quesito_atual ON proj_consultor_aval_quesito CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_proj_cons_aval_quesito_atual() RETURNS trigger AS $BODY$
DECLARE

BEGIN

if (NEW.ie_result_cb is not null)  and (NEW.ie_result_cb = 'S')  then
	select max(qt_peso)
	into STRICT	NEW.qt_ponto
	from	proj_avaliacao
	where	nr_sequencia	= NEW.nr_seq_quesito;
elsif (NEW.nr_seq_opcao is not null) then
	select max(qt_peso)
	into STRICT	NEW.qt_ponto
	from	proj_aval_opcao
	where	nr_sequencia	= NEW.nr_seq_opcao;
end if;
NEW.qt_ponto		:= coalesce(NEW.qt_ponto,0);

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_proj_cons_aval_quesito_atual() FROM PUBLIC;

CREATE TRIGGER proj_cons_aval_quesito_atual
	BEFORE INSERT OR UPDATE ON proj_consultor_aval_quesito FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_proj_cons_aval_quesito_atual();

