DROP TRIGGER IF EXISTS proj_orc_desp_atual ON proj_orc_desp CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_proj_orc_desp_atual() RETURNS trigger AS $BODY$
declare

vl_receita_total_w	double precision;
vl_imposto_w		double precision;
vl_margem_w		double precision;
vl_custo_total_w	double precision;

BEGIN

if (1=2) then

	vl_receita_total_w := NEW.vl_unitario * NEW.qt_unitaria;

	select	vl_receita_total_w,
		(vl_receita_total_w * 0.37),
		(vl_receita_total_w * 0.5),
		vl_receita_total_w
	into STRICT	vl_receita_total_w,
		vl_imposto_w,
		vl_margem_w,
		vl_custo_total_w
	;

	NEW.vl_custo_total	:= vl_custo_total_w;
	NEW.vl_receita_total	:= vl_receita_total_w;
	NEW.vl_imposto		:= vl_imposto_w;
	NEW.vl_margem		:= vl_margem_w;
end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
