DROP TRIGGER IF EXISTS proj_projeto_atualiza_gerente ON proj_equipe_papel CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_proj_projeto_atualiza_gerente() RETURNS trigger AS $BODY$
declare
ie_base_corp_w	varchar(1);
nr_seq_proj_w	proj_equipe.nr_seq_proj%type;

BEGIN

ie_base_corp_w := obter_se_base_corp;

if (ie_base_corp_w = 'S') and (NEW.nr_seq_funcao = 16)  then
	BEGIN
	
	select	nr_seq_proj
	into STRICT	nr_seq_proj_w
	from	proj_equipe
	where	nr_sequencia = NEW.nr_seq_equipe;

	update	proj_projeto
	set	cd_gerente_cliente = NEW.cd_pessoa_fisica
	where	nr_sequencia = nr_seq_proj_w;
	
	end;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
