CREATE TRIGGER proj_projeto_atualiza_gerente
	AFTER INSERT OR UPDATE ON proj_equipe_papel FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_proj_projeto_atualiza_gerente();

