SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS proj_projeto_atual ON proj_projeto CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_proj_projeto_atual() RETURNS trigger AS $BODY$
declare

nr_seq_ordem_serv_pai_w		man_ordem_servico.nr_seq_ordem_serv_pai%type;

BEGIN

select	max(mos.nr_seq_ordem_serv_pai)
into STRICT	nr_seq_ordem_serv_pai_w
from	man_ordem_servico mos
where	mos.nr_sequencia = NEW.nr_seq_ordem_serv;

if (nr_seq_ordem_serv_pai_w is not null) then
	BEGIN
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1085000);
	end;
end if;

if (NEW.nr_seq_estagio is not null) and (NEW.nr_seq_estagio <> coalesce(OLD.nr_seq_estagio,0)) then
	insert into proj_estagio_hist(nr_sequencia, nr_seq_proj, nr_seq_estagio,
		dt_historico, dt_atualizacao, nm_usuario,
		ie_status)
	values (	nextval('proj_estagio_hist_seq'), NEW.nr_sequencia, NEW.nr_seq_estagio,
		LOCALTIMESTAMP, LOCALTIMESTAMP, NEW.nm_usuario,
		NEW.ie_status);
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_proj_projeto_atual() FROM PUBLIC;

CREATE TRIGGER proj_projeto_atual
	BEFORE INSERT OR UPDATE ON proj_projeto FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_proj_projeto_atual();

