SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS proj_rat_ativ_atual ON proj_rat_ativ CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_proj_rat_ativ_atual() RETURNS trigger AS $BODY$
declare
qt_existe_w		bigint;
dt_inicio_rat_w		timestamp;
dt_final_rat_w		timestamp;

BEGIN
qt_existe_w	:= 0;

select	trunc(dt_inicio),
	trunc(dt_final)
into STRICT	dt_inicio_rat_w,
	dt_final_rat_w
from	proj_rat
where	nr_sequencia = NEW.nr_seq_rat;

if (NEW.dt_inicio_ativ is not null) and (trunc(NEW.dt_inicio_ativ) < dt_inicio_rat_w) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(262015); -- A data início da atividade não pode ser menor que a data de início do RAT !
end if;

if (NEW.dt_fim_ativ is not null) and (trunc(NEW.dt_fim_ativ) > dt_final_rat_w) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(262016); -- A data final da atividade não pode ser maior que a data final do RAT !
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_proj_rat_ativ_atual() FROM PUBLIC;

CREATE TRIGGER proj_rat_ativ_atual
	BEFORE INSERT OR UPDATE ON proj_rat_ativ FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_proj_rat_ativ_atual();

