declare

nr_sequencia_w 	bigint;

BEGIN
if (NEW.ie_status is not null) and
	((OLD.ie_status <> NEW.ie_status) or (OLD.ie_status is null)) then
	select	nextval('hist_status_prot_fornec_op_seq')
	into STRICT	nr_sequencia_w
	;

	insert into hist_status_prot_fornec_op(
		nr_sequencia,
		ie_status,
		nr_seq_protocolo,
		dt_atualizacao,
		nm_usuario)
	values (
		nr_sequencia_w,
		NEW.ie_status,
		OLD.nr_sequencia,
		LOCALTIMESTAMP,
		NEW.nm_usuario);

end if;

RETURN NEW;
end
