SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS protocolo_medic_hist_insert ON protocolo_medic_hist CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_protocolo_medic_hist_insert() RETURNS trigger AS $BODY$
declare
nr_versao_w				protocolo_medic_hist.nr_versao%type;
ie_status_w				protocolo_medic_hist.ie_status%type;
nr_seq_w				protocolo_medic_hist.nr_sequencia%type;

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'S')  then
	
	select coalesce(max(nr_versao), 1) nr_versao,
		max(nr_sequencia)
	into STRICT nr_versao_w,
		nr_seq_w
	from	protocolo_medic_hist
	where	nr_seq_medicacao = NEW.nr_seq_medicacao;

	select max(ie_status)
	into STRICT ie_status_w
	from protocolo_medic_hist
	where nr_sequencia = nr_seq_w;

	if (ie_status_w in ('AP', 'NA')) then
		NEW.nr_versao	:= nr_versao_w + 1;
	else
		NEW.nr_versao	:= nr_versao_w;
	end if;
	
end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_protocolo_medic_hist_insert() FROM PUBLIC;

CREATE TRIGGER protocolo_medic_hist_insert
	BEFORE INSERT ON protocolo_medic_hist FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_protocolo_medic_hist_insert();

