CREATE OR REPLACE FUNCTION trigger_fct_protocolo_medic_proc_atual() RETURNS trigger AS $BODY$
declare
ie_situacao_w	varchar(1);
BEGIN
  BEGIN
BEGIN

if (NEW.cd_procedimento is null) and (NEW.nr_seq_proc_interno is null) and (NEW.nr_seq_exame is null) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(201161);
end if;

if (obter_se_prot_lib_regras = 'S') then
	update	protocolo_medicacao
	set	nm_usuario_aprov	 = NULL,
		dt_aprovacao		 = NULL,
		ie_status		=	'PA'
	where	cd_protocolo		=	NEW.cd_protocolo
	and	nr_sequencia		=	NEW.nr_sequencia;
end if;

if (NEW.NR_SEQ_EXAME is not null) then
	select	coalesce(max(ie_situacao),'A')
	into STRICT	ie_situacao_w
	from	exame_laboratorio
	where	nr_seq_exame = NEW.NR_SEQ_EXAME;

	if (ie_situacao_w = 'I') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(201160);
	end if;
end if;

if (NEW.hr_prim_horario is not null) and ((NEW.hr_prim_horario <> OLD.hr_prim_horario) or (OLD.dt_prim_horario is null)) then
	NEW.dt_prim_horario := to_date(to_char(LOCALTIMESTAMP,'dd/mm/yyyy') || ' ' || NEW.hr_prim_horario,'dd/mm/yyyy hh24:mi');
end if;
exception
	when others then
	null;
end;

  END;
RETURN NEW;
end
