DROP TRIGGER IF EXISTS protocolo_solic_indic_atual ON protocolo_solic_indicacao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_protocolo_solic_indic_atual() RETURNS trigger AS $BODY$
declare

cd_protocolo_w	bigint;
nr_sequencia_w	bigint;

BEGIN

if (obter_se_prot_lib_regras = 'S') then
	select	cd_protocolo,
		nr_seq_protocolo
	into STRICT	cd_protocolo_w,
		nr_sequencia_w
	from	prot_solic_bco_sangue
	where	nr_sequencia	= NEW.nr_seq_solic_bs;

	update	protocolo_medicacao
	set	nm_usuario_aprov	 = NULL,
		dt_aprovacao		 = NULL,
		ie_status		=	'PA'
	where	cd_protocolo		=	cd_protocolo_w
	and	nr_sequencia		=	nr_sequencia_w;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
