CREATE TRIGGER protocolo_solic_indic_atual
	BEFORE INSERT OR UPDATE ON protocolo_solic_indicacao FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_protocolo_solic_indic_atual();

