DROP TRIGGER IF EXISTS prp_etapa_processo_update ON prp_etapa_processo CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_prp_etapa_processo_update() RETURNS trigger AS $BODY$
declare
qt_registros_w	bigint;

BEGIN

if (NEW.ie_situacao = 'I') then

	select	count(*)
	into STRICT	qt_registros_w
	from	PRP_ETAPA_DEPENDENCIA
	where	NR_SEQ_ETAPA_PROCESSO = NEW.nr_sequencia;

	if (qt_registros_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(309514);
	end if;

	select	count(*)
	into STRICT	qt_registros_w
	from	PRP_NECESSIDADE_ETAPA
	where	NR_SEQ_ETAPA_PROCESSO = NEW.nr_sequencia;

	if (qt_registros_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(309514);
	end if;

end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_prp_etapa_processo_update() FROM PUBLIC;

CREATE TRIGGER prp_etapa_processo_update
	BEFORE UPDATE ON prp_etapa_processo FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_prp_etapa_processo_update();

