SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS ptu_fatura_delete_atual ON ptu_fatura CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_ptu_fatura_delete_atual() RETURNS trigger AS $BODY$
declare

qt_titulo_w	integer;

BEGIN
-- Contar títulos abertos de fatura A500
select	count(1)
into STRICT	qt_titulo_w
from	titulo_pagar
where	nr_titulo = OLD.nr_titulo
and	ie_situacao <> 'C';

-- Consistir títulos aberto
if (coalesce(qt_titulo_w,0) > 0) then
	--A fatura não pode ser excluída. Existem títulos em aberto para esta fatura!
	CALL wheb_mensagem_pck.exibir_mensagem_abort(356067);
end if;

-- Contar títulos NDR abertos de fatura A500
select	count(1)
into STRICT	qt_titulo_w
from	titulo_pagar
where	nr_titulo = OLD.nr_titulo_ndc
and	ie_situacao <> 'C';

-- Consistir títulos NDR aberto
if (coalesce(qt_titulo_w,0) > 0) then
	--A fatura não pode ser excluída. Existem títulos em aberto para esta fatura!
	CALL wheb_mensagem_pck.exibir_mensagem_abort(356067);
end if;

RETURN OLD;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_ptu_fatura_delete_atual() FROM PUBLIC;

CREATE TRIGGER ptu_fatura_delete_atual
	BEFORE DELETE ON ptu_fatura FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_ptu_fatura_delete_atual();

