DROP TRIGGER IF EXISTS ptu_nota_servico_delete ON ptu_nota_servico CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_ptu_nota_servico_delete() RETURNS trigger AS $BODY$
declare

ds_log_w	varchar(4000);
ds_log_call_w	varchar(1500);
nr_seq_fatura_w	ptu_fatura.nr_sequencia%type;

BEGIN

	select 	nr_seq_fatura
	into STRICT	nr_seq_fatura_w
	from	ptu_nota_cobranca
	where	nr_sequencia = OLD.nr_seq_nota_cobr;

	ds_log_call_w := substr(' Função ativa : '|| obter_funcao_ativa || chr(13) ||chr(10)||
				' CallStack: '|| chr(13) || chr(10)|| dbms_utility.format_call_stack,1,1500);

	ds_log_w := 'Nota cobrança = '||OLD.nr_seq_nota_cobr||chr(13) ||chr(10)||ds_log_call_w;

	insert into pls_fatura_log(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_lote,
		ds_log,
		ds_observacao,
		ie_opcao)
	values (	nextval('pls_fatura_log_seq'),
		LOCALTIMESTAMP,
		OLD.nm_usuario,
		LOCALTIMESTAMP,
		OLD.nm_usuario,
		nr_seq_fatura_w,
		ds_log_w,
		null,
		null);
RETURN OLD;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
