DROP TRIGGER IF EXISTS ptu_nota_servico_insert ON ptu_nota_servico CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_ptu_nota_servico_insert() RETURNS trigger AS $BODY$
declare

nr_seq_conta_pos_w	pls_conta_pos_estabelecido.nr_sequencia%type;
nr_seq_conta_w		pls_conta.nr_sequencia%type;

BEGIN

if (TG_OP = 'INSERT') then
	NEW.ds_stack := substr(dbms_utility.format_call_stack,1,2000);
end if;

if (NEW.vl_procedimento < 0) or (NEW.vl_filme < 0) or (NEW.vl_custo_operacional < 0) or (NEW.vl_adic_procedimento < 0) or (NEW.vl_adic_filme < 0) or (NEW.vl_adic_co < 0) then
	
	if (NEW.nr_seq_conta_proc is not null) then
		select	max(nr_sequencia),
			max(nr_seq_conta)
		into STRICT	nr_seq_conta_pos_w,
			nr_seq_conta_w
		from	pls_conta_pos_estabelecido
		where	nr_seq_conta_proc = NEW.nr_seq_conta_proc;
	
	elsif (NEW.nr_seq_conta_mat is not null) then
		select	max(nr_sequencia),
			max(nr_seq_conta)
		into STRICT	nr_seq_conta_pos_w,
			nr_seq_conta_w
		from	pls_conta_pos_estabelecido
		where	nr_seq_conta_mat = NEW.nr_seq_conta_mat;
	end if;
	
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 281537, 'NR_SEQ_CONTA=' || nr_seq_conta_w || ';' || 'NR_SEQ_CONTA_POS=' || nr_seq_conta_pos_w );
end if;

NEW.ds_servico_aux	:= coalesce(NEW.ds_servico_aux,upper(trim(both ptu_somente_caracter_permitido(NEW.ds_servico,'AN'))));
NEW.nr_nota_numerico	:= somente_numero(coalesce(NEW.nr_nota,OLD.nr_nota));	

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
