DROP TRIGGER IF EXISTS qt_bloqueio_atual ON qt_bloqueio CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_qt_bloqueio_atual() RETURNS trigger AS $BODY$
DECLARE
dt_atualizacao 	timestamp	:= LOCALTIMESTAMP;
BEGIN
  BEGIN
if (NEW.dt_final < NEW.dt_inicial) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(262398);
end if;

BEGIN

delete	from quimio_controle_horario
where	dt_agenda >= trunc(LOCALTIMESTAMP)
and (nr_seq_local	= NEW.nr_seq_local or NEW.nr_seq_local is null);

exception
	when others then
      	dt_atualizacao := LOCALTIMESTAMP;
end;
  END;
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_qt_bloqueio_atual() FROM PUBLIC;

CREATE TRIGGER qt_bloqueio_atual
	BEFORE INSERT OR UPDATE ON qt_bloqueio FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_qt_bloqueio_atual();

