SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS qua_doc_doc_insert ON qua_doc_doc CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_qua_doc_doc_insert() RETURNS trigger AS $BODY$
DECLARE

nm_documento_w		varchar(255);

BEGIN

select	nm_documento
into STRICT	nm_documento_w
from	qua_documento
where	nr_sequencia = NEW.nr_seq_doc_rel;

if (NEW.nr_seq_doc = NEW.nr_seq_doc_rel) then
	--'Documento: ' || nm_documento_w || chr(13) ||
		--	'Este documento já é o documento principal, portanto não pode ser utilizado por ele mesmo !');
	CALL wheb_mensagem_pck.exibir_mensagem_abort(264053,'NR_DOCUMENTO='||nm_documento_w);
end if;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_qua_doc_doc_insert() FROM PUBLIC;

CREATE TRIGGER qua_doc_doc_insert
	BEFORE INSERT ON qua_doc_doc FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_qua_doc_doc_insert();

