SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS qua_evento_pac_pend_atual ON qua_evento_paciente CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_qua_evento_pac_pend_atual() RETURNS trigger AS $BODY$
declare
 
qt_reg_w		smallint;
ie_tipo_w		varchar(10);
ie_lib_evento_w	varchar(10);
BEGIN
  BEGIN 
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N') then 
	goto Final;
end if;
 
BEGIN 
	if (TG_OP = 'INSERT') or (TG_OP = 'UPDATE') then 
 
    select	max(ie_lib_evento) 
    into STRICT	ie_lib_evento_w 
    from	parametro_medico 
    where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;
 
    if (coalesce(ie_lib_evento_w,'N') = 'S') then 
      if (NEW.dt_liberacao is null) then 
        ie_tipo_w := 'EVT';
      elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then 
        ie_tipo_w := 'XEVT';
      end if;
      if (ie_tipo_w	is not null) then 
        CALL Gerar_registro_pendente_PEP(ie_tipo_w, NEW.nr_sequencia, coalesce(NEW.cd_pessoa_fisica,substr(obter_pessoa_atendimento(NEW.nr_atendimento,'C'),1,255)), NEW.nr_atendimento, NEW.nm_usuario);
      end if;
    end if;
    
   elsif (TG_OP = 'DELETE') then 
		 
		delete	from pep_item_pendente 
		where 	IE_TIPO_REGISTRO = 'EVT' 
		and	  nr_seq_registro = OLD.nr_sequencia 
		and	  coalesce(IE_TIPO_PENDENCIA,'L')	= 'L';
		 
	end if;
    
 
exception 
when others then 
	null;
end;
	 
<<Final>> 
qt_reg_w	:= 0;
  END;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_qua_evento_pac_pend_atual() FROM PUBLIC;

CREATE TRIGGER qua_evento_pac_pend_atual
	AFTER INSERT OR UPDATE ON qua_evento_paciente FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_qua_evento_pac_pend_atual();

