SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS qua_nao_conformidade_atual ON qua_nao_conformidade CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_qua_nao_conformidade_atual() RETURNS trigger AS $BODY$
declare
ie_anonimo_w	qua_tipo_nao_conform.ie_anonimo%type;
qt_reg_w	bigint;

BEGIN

select 	max(ie_anonimo)
into STRICT	ie_anonimo_w
from 	qua_tipo_nao_conform
where 	nr_sequencia = NEW.nr_seq_tipo;

if (ie_anonimo_w = 'S') then
	BEGIN
	NEW.nm_usuario		:= obter_desc_expressao(710876);
	NEW.nm_usuario_nrec	:= obter_desc_expressao(710876);
	NEW.nm_usuario_origem	:= obter_desc_expressao(710876);
	end;
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_qua_nao_conformidade_atual() FROM PUBLIC;

CREATE TRIGGER qua_nao_conformidade_atual
	BEFORE INSERT OR UPDATE ON qua_nao_conformidade FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_qua_nao_conformidade_atual();

