DROP TRIGGER IF EXISTS qua_tipo_evento_after ON qua_tipo_evento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_qua_tipo_evento_after() RETURNS trigger AS $BODY$
declare

qt_reg_w		smallint;
qt_existe_w	bigint;

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger = 'N')  then
	goto Final;
end if;

if (NEW.ie_situacao = 'I') then
	BEGIN
	select	count(*)
	into STRICT	qt_existe_w
	from	qua_evento
	where	nr_seq_tipo = NEW.nr_sequencia;

	if (qt_existe_w > 0) then
		update	qua_evento
		set	ie_situacao = NEW.ie_situacao
		where	nr_seq_tipo = NEW.nr_sequencia;
	end if;
	end;
end if;

<<Final>>
qt_reg_w := 0;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
