DROP TRIGGER IF EXISTS que_questao_insert ON que_questao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_que_questao_insert() RETURNS trigger AS $BODY$
declare
 
qt_nivel_w	smallint;
 
BEGIN 
 
if (NEW.NR_SEQ_QUESTAO_SUP is null) then 
	NEW.qt_nivel := 1;
else	 
	select	qt_nivel 
	into STRICT	qt_nivel_w 
	from	que_questao 
	where	nr_sequencia = NEW.NR_SEQ_QUESTAO_SUP;
	 
	NEW.qt_nivel := coalesce(qt_nivel_w, 0) + 1;
end if;
 
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
