DROP TRIGGER IF EXISTS que_questao_update ON que_questao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_que_questao_update() RETURNS trigger AS $BODY$
declare
 
C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	que_questao 
	where	nr_seq_questao_sup = NEW.nr_sequencia;
 
nivel_w			smallint := 0;
nr_seq_inferior_w	bigint;
 
pragma autonomous_transaction;
 
BEGIN 
if (NEW.NR_SEQ_QUESTAO_SUP is null) then 
	NEW.qt_nivel := 1;
else	 
	if (coalesce(OLD.NR_SEQ_QUESTAO_SUP, 0) <> coalesce(NEW.NR_SEQ_QUESTAO_SUP, 0)) then 
		 
		select	qt_nivel 
		into STRICT	nivel_w 
		from	que_questao 
		where	nr_sequencia = NEW.NR_SEQ_QUESTAO_SUP;
		 
		NEW.qt_nivel := coalesce(nivel_w, 0) + 1;
	end if;
end if;
 
open C01;
loop 
fetch	C01 into	 
	nr_seq_inferior_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	BEGIN 
	 
	update	que_questao 
	set	qt_nivel = NEW.qt_nivel +1 
	where	nr_sequencia = nr_seq_inferior_w;	
	 
	end;
end loop;
close C01;
 
commit;
 
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_que_questao_update() FROM PUBLIC;

CREATE TRIGGER que_questao_update
	BEFORE UPDATE ON que_questao FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_que_questao_update();

