	from	natureza_gasto
	where	nr_sequencia = NEW.nr_seq_ng_origem;

	exception when others then
		ie_situacao_w := '';
	end;

	if (ie_situacao_w = 'I') then
		-- A natureza origem informada está inativa.
		CALL wheb_mensagem_pck.exibir_mensagem_abort(277791);
	end if;
end if;

if (NEW.nr_seq_ng_destino is not null) then
	BEGIN
	select	ie_situacao
