CREATE OR REPLACE FUNCTION trigger_fct_regra_momento_lib_befinsupd() RETURNS trigger AS $BODY$
DECLARE

ie_tipo_item_w		varchar(15);

BEGIN

select	coalesce(max(ie_tipo_item),'X')
into STRICT	ie_tipo_item_w
from	regra_disp_lote_farm
where	nr_sequencia	= NEW.nr_seq_regra;

if (ie_tipo_item_w			<> 'IA') and (NEW.nr_seq_proc_interno	is not null) then
