SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS regra_numeracao_decla_aftins ON regra_numeracao_declaracao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_regra_numeracao_decla_aftins() RETURNS trigger AS $BODY$
declare
nr_inicial_w	bigint;
NR_FINAL_w	bigint;
BEGIN
if (NEW.NR_INICIAL	> NEW.NR_FINAL) then
	--  A numeração inicial está maior que a numeração final.
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(264549);
end if;

nr_inicial_w	:= NEW.NR_INICIAL;
NR_FINAL_w	:= NEW.NR_FINAL;

for i in nr_inicial_w..NR_FINAL_w loop
	BEGIN

	insert into REGRA_NUMERACAO_DEC_ITEM(	nr_sequencia,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							nr_declaracao,
							ie_disponivel,
							nr_seq_regra_num)
				values (	nextval('regra_numeracao_dec_item_seq'),
							LOCALTIMESTAMP,
							NEW.nm_usuario,
							LOCALTIMESTAMP,
							NEW.nm_usuario,
							i,
							'S',
							NEW.nr_sequencia);

	end;
end loop;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_regra_numeracao_decla_aftins() FROM PUBLIC;

CREATE TRIGGER regra_numeracao_decla_aftins
	AFTER INSERT ON regra_numeracao_declaracao FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_regra_numeracao_decla_aftins();

